import numpy as np
from Util import variables as vars

float32 = np.float32

def deg_to_rad(deg : float32) -> float32:
    return (deg * np.pi) / 180.0

def rad_to_deg(rad : float32) -> float32:
    return (rad * 180.0) / np.pi

def idx_to_rad(idx : int) -> float32:
    return idx * vars.FOV / (vars.RANGE_SIZE - 1) - vars.FOV / 2.0

def rad_to_idx(rad : float32) -> int:
    return int(np.round((rad + vars.FOV / 2.0) * (vars.RANGE_SIZE / vars.FOV)))